// Enhanced response formatter with tool display

// Helper function to determine retriever purpose
function getRetrieverPurpose(retrieverType) {
  const purposes = {
    'vector': 'ðŸ” Semantic Search - Finding conceptually similar content',
    'graph': 'ðŸ•¸ï¸ Graph Traversal - Exploring entity relationships',
    'hybrid_sequential': 'ðŸ”„ Sequential Hybrid - Vector search followed by graph exploration',
    'hybrid_parallel': 'âš¡ Parallel Hybrid - Simultaneous vector and graph search',
    'text2cypher': 'ðŸ’¬ Natural Language to Cypher - Converting questions to graph queries',
    'no_retrieval': 'ðŸ¤– Direct Response - No retrieval needed'
  };
  return purposes[retrieverType] || 'â“ Unknown retrieval method';
}

// Parse the API response
const apiResponse = $input.first().json;

// Format the enhanced response
const formattedResponse = {
  success: true,
  timestamp: new Date().toISOString(),

  // Main response
  response: apiResponse.response,

  // Retrieval metadata
  retrieval_info: {
    retriever_used: apiResponse.retriever_used,
    purpose: getRetrieverPurpose(apiResponse.retriever_used),
    confidence: apiResponse.confidence,
    context_items_count: apiResponse.context_items
  },

  // Query metadata
  query_metadata: {
    original_query: $('webhook-entry').first().json.query,
    processing_time_ms: Date.now() - new Date($('webhook-entry').first().json.timestamp).getTime(),
    top_k_requested: $('webhook-entry').first().json.top_k || 5
  },

  // Tool usage summary
  tool_summary: {
    strategy: apiResponse.retriever_used,
    description: `The system used ${apiResponse.retriever_used} retrieval based on query pattern analysis`,
    confidence_level: apiResponse.confidence > 0.8 ? 'High' : apiResponse.confidence > 0.6 ? 'Medium' : 'Low'
  },

  // Optional debugging info
  debug_info: {
    router_version: 'neo4j-graphrag-python-v1',
    api_endpoint: $('call-graphrag-api').first().json.url,
    workflow_execution_id: $execution.id
  }
};

// Add performance insights
if (formattedResponse.query_metadata.processing_time_ms > 5000) {
  formattedResponse.performance_warning = 'Query took longer than expected. Consider optimizing indexes.';
}

// Add retrieval insights
if (apiResponse.context_items === 0) {
  formattedResponse.retrieval_warning = 'No context items found. Consider refining the query.';
}

return formattedResponse;

// Log successful query for monitoring
const logEntry = {
  timestamp: new Date().toISOString(),
  query: $json.query_metadata.original_query,
  retriever: $json.retrieval_info.retriever_used,
  confidence: $json.retrieval_info.confidence,
  context_items: $json.retrieval_info.context_items_count,
  processing_time_ms: $json.query_metadata.processing_time_ms,
  success: true
};

// In production, this would send to a logging service
console.log('Query Log:', JSON.stringify(logEntry));

// Pass through the response
return $json;

// Handle API errors gracefully
const error = $input.first().json.error || $input.first().json;

const errorResponse = {
  success: false,
  timestamp: new Date().toISOString(),
  error: {
    message: error.message || 'An unexpected error occurred',
    type: error.name || 'UnknownError',
    details: error.description || error.details || null
  },
  query_metadata: {
    original_query: $('webhook-entry').first().json.query,
    processing_time_ms: Date.now() - new Date($('webhook-entry').first().json.timestamp).getTime()
  },
  fallback_response: 'I apologize, but I encountered an error processing your query. Please try rephrasing your question or contact support if the issue persists.',
  debug_info: {
    workflow_execution_id: $execution.id,
    error_node: $node.name,
    api_endpoint: $('call-graphrag-api').first().json.url
  }
};

// Log error for monitoring
console.error('Query Error:', JSON.stringify({
  timestamp: errorResponse.timestamp,
  query: errorResponse.query_metadata.original_query,
  error: errorResponse.error.message,
  execution_id: errorResponse.debug_info.workflow_execution_id
}));

return errorResponse;
