values.content:
You are an entity extraction specialist. Extract relevant entities and their types from the user query. Return JSON format:
{
  "entities": [
    {"name": "entity_name", "type": "Customer|Product|Team|Project|Risk"}
  ],
  "query_type": "revenue_risk|commitments|relationships|general",
  "metrics_needed": ["mrr", "risk_score", "sla_compliance"]
}

parameters.jsCode:
// Build Neo4j Cypher query based on extracted entities
const extraction = JSON.parse($json.choices[0].message.content);
const entities = extraction.entities;
const queryType = extraction.query_type;

let cypherQuery = '';
let parameters = {};

switch(queryType) {
  case 'revenue_risk':
    cypherQuery = `
      MATCH (c:Customer)-[sub:HAS_SUBSCRIPTION]->(p:Product)
      WHERE c.name IN $customerNames
      OPTIONAL MATCH (c)-[:HAS_RISK]->(r:Risk)
      OPTIONAL MATCH (c)-[:HAS_SLA]->(sla:SLA)
      RETURN c.name AS customer,
             sub.mrr AS monthly_revenue,
             r.impact_factor AS risk_factor,
             sub.mrr * COALESCE(r.impact_factor, 0) AS revenue_at_risk,
             sla.compliance AS sla_compliance
    `;
    parameters.customerNames = entities.filter(e => e.type === 'Customer').map(e => e.name);
    break;

  case 'commitments':
    cypherQuery = `
      MATCH (c:Customer)-[sub:HAS_SUBSCRIPTION]->(p:Product)
      WHERE c.name IN $customerNames
      OPTIONAL MATCH (c)-[:COMMITTED_FEATURE]->(f:Feature)
      OPTIONAL MATCH (f)-[:HAS_RISK]->(r:Risk)
      RETURN c.name AS customer,
             sub.mrr AS revenue,
             collect(DISTINCT {feature: f.name, deadline: f.deadline}) AS commitments,
             collect(DISTINCT r.description) AS risks
    `;
    parameters.customerNames = entities.filter(e => e.type === 'Customer').map(e => e.name);
    break;

  case 'relationships':
    cypherQuery = `
      MATCH path = (start)-[*1..3]-(end)
      WHERE start.name IN $entityNames
      RETURN start.name AS source,
             [rel in relationships(path) | type(rel)] AS relationship_types,
             end.name AS target,
             labels(end)[0] AS target_type,
             length(path) AS distance
      ORDER BY distance
      LIMIT 20
    `;
    parameters.entityNames = entities.map(e => e.name);
    break;

  default:
    // Vector similarity search for general queries
    cypherQuery = `
      CALL db.index.vector.queryNodes('document_embeddings', 10, $queryEmbedding)
      YIELD node AS doc, score
      WHERE score > 0.7
      MATCH (doc)-[:MENTIONS]->(e:Entity)
      RETURN doc.content, e.name, labels(e)[0] AS entity_type, score
      ORDER BY score DESC
    `;
    // Note: queryEmbedding will be added in the next node
}

return {
  cypherQuery,
  parameters,
  queryType,
  needsEmbedding: queryType === 'general'
};

// Merge embedding with query if needed
const queryData = $('build_query').item.json;
const embedding = $json;

if (queryData.needsEmbedding) {
  queryData.parameters.queryEmbedding = embedding;
}

return queryData;

