parameters.jsCode:
// Enhanced response formatting with tool display fix
const aiResponse = $json.choices[0].message.content;
const queryType = $('build_query').item.json.queryType;
const entities = JSON.parse($('extract_entities').item.json.choices[0].message.content).entities;
const queryResults = $('execute_neo4j_query').item.json.results[0].data;

// Tool display fix - getPurpose function
function getPurpose(queryType, entities) {
  const toolsUsed = [];

  switch(queryType) {
    case 'revenue_risk':
      toolsUsed.push('ðŸ” Graph Search: Customer Revenue Analysis');
      toolsUsed.push('âš ï¸ Risk Assessment: Revenue Impact Calculation');
      break;
    case 'commitments':
      toolsUsed.push('ðŸ” Graph Search: Customer Commitments');
      toolsUsed.push('ðŸ“… Timeline Analysis: Feature Deadlines');
      break;
    case 'relationships':
      toolsUsed.push('ðŸ” Graph Search: Entity Relationships');
      toolsUsed.push('ðŸŒ Network Analysis: Connection Mapping');
      break;
    case 'general':
      toolsUsed.push('ðŸ“š Vector Search: Document Similarity');
      toolsUsed.push('ðŸ” Entity Extraction: Mentioned Entities');
      break;
  }

  if (entities && entities.length > 0) {
    toolsUsed.push(`ðŸ·ï¸ Entities Found: ${entities.map(e => e.name).join(', ')}`);
  }

  return toolsUsed.join('\n');
}

// Format the complete response
const toolsDisplay = getPurpose(queryType, entities);
const resultsCount = queryResults ? queryResults.length : 0;

const formattedResponse = `${aiResponse}

**Tools Used:**
${toolsDisplay}

**Query Stats:**
- Query Type: ${queryType}
- Results Found: ${resultsCount}
- Entities Detected: ${entities.length}`;

return [{
  json: {
    response: formattedResponse,
    metadata: {
      queryType,
      toolsUsed: toolsDisplay,
      entitiesFound: entities,
      resultsCount
    }
  }
}];
