// Extract webhook data
const items = $input.all();
const webhookData = items[0].json.body;

const userMessage = webhookData.message || "No message provided";
const correlationId = webhookData.correlation_id || "no-correlation-id";
const userId = webhookData.user_id || "anonymous";
const callbackUrl = webhookData.callback_url || "";

return [{
  json: {
    chatInput: userMessage,
    correlation_id: correlationId,
    user_id: userId,
    user_message: userMessage,
    callback_url: callbackUrl,
    processed_at: new Date().toISOString()
  }
}];

// Simulated Knowledge Base Search Tool
// In production, this would call an actual KB API or database

const query = $input.query || "";

// Simulated KB articles
const knowledgeBase = [
  {
    id: "KB001",
    title: "How to Reset Your Password",
    tags: ["password", "reset", "login", "authentication", "forgot"],
    solution: "1. Click 'Forgot Password' on login page\n2. Enter your email\n3. Check email for reset link\n4. Create new password (min 8 chars)\n5. Try logging in with new password",
    commonIssues: ["password expired", "forgot password", "can't login", "reset password"]
  },
  {
    id: "KB002",
    title: "Login Troubleshooting Guide",
    tags: ["login", "access", "authentication", "troubleshooting"],
    solution: "1. Clear browser cache and cookies\n2. Try incognito/private mode\n3. Disable browser extensions\n4. Check if account is locked\n5. Verify correct username/email\n6. Contact support if issue persists",
    commonIssues: ["can't log in", "login failed", "access denied", "login error"]
  },
  {
    id: "KB003",
    title: "Account Locked - What to Do",
    tags: ["account", "locked", "security", "access"],
    solution: "Account locks after 5 failed attempts. Wait 30 minutes or contact support for immediate unlock. Check email for security alert.",
    commonIssues: ["account locked", "too many attempts", "locked out"]
  },
  {
    id: "KB004",
    title: "Two-Factor Authentication Issues",
    tags: ["2fa", "authentication", "security", "code"],
    solution: "1. Ensure correct time on device\n2. Use backup codes if available\n3. Try SMS instead of app\n4. Contact support to reset 2FA",
    commonIssues: ["2fa not working", "authentication code", "can't get code"]
  }
];

// Simple search algorithm
const searchTerms = query.toLowerCase().split(' ');
const results = knowledgeBase.map(article => {
  let score = 0;

  // Check title
  searchTerms.forEach(term => {
    if (article.title.toLowerCase().includes(term)) score += 3;
  });

  // Check tags
  searchTerms.forEach(term => {
    if (article.tags.some(tag => tag.includes(term))) score += 2;
  });

  // Check common issues
  searchTerms.forEach(term => {
    if (article.commonIssues.some(issue => issue.includes(term))) score += 1;
  });

  return { ...article, score };
})
.filter(article => article.score > 0)
.sort((a, b) => b.score - a.score)
.slice(0, 3);

if (results.length === 0) {
  return JSON.stringify({
    articlesFound: 0,
    message: "No relevant articles found",
    suggestion: "Please contact support for assistance"
  });
}

const response = {
  articlesFound: results.length,
  topMatch: {
    id: results[0].id,
    title: results[0].title,
    solution: results[0].solution,
    relevanceScore: results[0].score
  },
  allResults: results.map(r => ({
    id: r.id,
    title: r.title,
    score: r.score
  }))
};

return JSON.stringify(response);

// Parse intent evaluation and prepare for routing
 const aiResponse = $input.first().json;
 const originalData = $node["Process Message"].json;

 // Extract intent evaluation
 let intentData = {};
 try {
   // The output field contains the JSON string
   const aiText = aiResponse.output;

   if (!aiText) {
     throw new Error('No AI response text found in output field');
   }

   // Parse the JSON response
   intentData = JSON.parse(aiText);

 } catch (e) {
   console.error('Failed to parse intent:', e);
   console.error('Raw response:', aiResponse);
   intentData = { intent: 'GENERAL_INQUIRY', confidence: 0.5 };
 }

 // Prepare data for routing
 return [{
   json: {
     ...originalData,
     intentEvaluation: intentData,
     intent: intentData.intent || 'GENERAL_INQUIRY',
     confidence: intentData.confidence || 0.5,
     urgency: intentData.urgency || 'MEDIUM',
     entities: intentData.entities || [],
     suggestedAction: intentData.suggestedAction || 'Route to general support',
     contextFromHistory: intentData.contextFromHistory || '',
     relatedTickets: intentData.relatedTickets || [],
     isFollowUp: Boolean(intentData.isFollowUp),
     escalationReason: intentData.escalationReason || '',
     knowledgeBaseResults: intentData.knowledgeBaseResults || null
   }
 }];

