// Technical Support Handler with Context and KB Results
const data = $input.first().json;

// Build context-aware message
let contextMessage = '';
if (data.isFollowUp && data.relatedTickets.length > 0) {
  contextMessage = `\n\n**Previous Issues:** This appears to be related to ${data.relatedTickets.join(', ')}. ${data.contextFromHistory}`;
}

// Build KB results message
let kbMessage = '';
if (data.knowledgeBaseResults && data.knowledgeBaseResults.articlesFound > 0) {
  const kb = data.knowledgeBaseResults;
  kbMessage = `\n\n**Suggested Solution from Knowledge Base:**\nðŸ“š ${kb.topArticle}\n\n${kb.solution}\n\nThis solution has helped 89% of users with similar issues.`;
}

// Process technical support request
const response = {
  ...data,
  handled_by: 'technical_support',
  actions_taken: [
    'Searched knowledge base for: ' + (data.entities || []).join(', '),
    data.knowledgeBaseResults?.articlesFound > 0 ? 
      `Found ${data.knowledgeBaseResults.articlesFound} relevant KB articles` : 
      'No direct KB matches found',
    data.isFollowUp ? 'Reviewed previous tickets: ' + data.relatedTickets.join(', ') : 'Created new support ticket',
    data.urgency === 'HIGH' ? 'Escalated to senior tech team' : 'Assigned to tech team',
    'Sent acknowledgment to user'
  ],
  response_message: `We've received your technical support request. ${data.isFollowUp ? 'We see this is related to your previous issue.' : 'A support ticket has been created.'}${kbMessage}\n\nIf the suggested solution doesn't resolve your issue, our technical team will assist you shortly. Your ticket ID is: TECH-${Date.now()}${contextMessage}`,
  kb_article_provided: data.knowledgeBaseResults?.articlesFound > 0,
  kb_article_id: data.knowledgeBaseResults?.topArticle || null
};

return [{ json: response }];

// Billing Questions Handler with Context
const data = $input.first().json;

// Build context-aware response
let contextMessage = '';
if (data.contextFromHistory) {
  contextMessage = `\n\n**Context:** ${data.contextFromHistory}`;
}

// Process billing question
const response = {
  ...data,
  handled_by: 'billing_department',
  actions_taken: [
    'Checked account status',
    data.isFollowUp ? 'Reviewed conversation history' : 'Reviewed payment history',
    'Prepared billing summary',
    'Forwarded to finance team'
  ],
  response_message: `Thank you for your billing inquiry. ${data.isFollowUp ? 'Following up on your previous question,' : ''} Our finance team will review your account and respond within 24 hours. Reference number: BILL-${Date.now()}${contextMessage}`
};

return [{ json: response }];

// Feature Request Handler
const data = $input.first().json;

// Process feature request
const response = {
  ...data,
  handled_by: 'product_team',
  actions_taken: [
    'Logged feature request',
    'Assessed feasibility',
    'Added to product backlog',
    'Sent thank you message'
  ],
  response_message: `Thank you for your feature suggestion! We've logged your request and our product team will review it. You'll receive updates on its status. Request ID: FEAT-${Date.now()}`
};

return [{ json: response }];

// Complaint Handler with Escalation Context
const data = $input.first().json;

// Determine if this needs escalation based on history
const needsEscalation = data.relatedTickets.length > 1 || data.escalationReason;

// Process complaint
const response = {
  ...data,
  handled_by: needsEscalation ? 'senior_customer_service' : 'customer_service',
  actions_taken: [
    'Logged complaint with high priority',
    needsEscalation ? 'ESCALATED to senior management' : 'Created incident report',
    data.relatedTickets.length > 0 ? `Linked to previous issues: ${data.relatedTickets.join(', ')}` : 'First complaint from user',
    'Assigned to customer service manager',
    'Sent apology and acknowledgment'
  ],
  response_message: `We sincerely apologize for your experience. ${needsEscalation ? 'Due to the recurring nature of this issue, we have escalated it to senior management.' : 'Your complaint has been logged.'} A ${needsEscalation ? 'senior' : ''} customer service manager will contact you within ${needsEscalation ? '2' : '4'} hours. Incident ID: COMP-${Date.now()}\n\n${data.escalationReason ? `Escalation reason: ${data.escalationReason}` : ''}`
};

return [{ json: response }];

// General Inquiry / Fallback Handler
const data = $input.first().json;

// Process general inquiry
const response = {
  ...data,
  handled_by: 'general_support',
  actions_taken: [
    'Checked FAQ database',
    'Prepared general information',
    'Identified best team for routing',
    'Sent standard response'
  ],
  response_message: `Thank you for contacting us. We've received your inquiry and will route it to the appropriate team. You'll receive a response within 24-48 hours. Reference: GEN-${Date.now()}`
};

return [{ json: response }];

// HIGH PRIORITY Escalation Handler - For repeated issues or high urgency follow-ups
const data = $input.first().json;

// Build escalation context
const escalationContext = [];
if (data.isFollowUp) escalationContext.push('Unresolved follow-up issue');
if (data.relatedTickets.length > 2) escalationContext.push(`Pattern of ${data.relatedTickets.length} related issues`);
if (data.escalationReason) escalationContext.push(data.escalationReason);

// Process high-priority escalation
const response = {
  ...data,
  handled_by: 'executive_escalation',
  priority: 'CRITICAL',
  actions_taken: [
    'IMMEDIATE ESCALATION TRIGGERED',
    'Notified executive team',
    'Created priority incident with full history',
    `Linked previous tickets: ${data.relatedTickets.join(', ')}`,
    'Initiated emergency response protocol',
    'Assigned senior manager',
    'Scheduled immediate callback'
  ],
  response_message: `We take your concern extremely seriously. This has been immediately escalated to our executive team due to: ${escalationContext.join(', ')}. A senior manager will contact you within 30 minutes.\n\nPriority Case ID: URGENT-${Date.now()}\n\nRelated Issues: ${data.relatedTickets.join(', ')}\n\nWe apologize for the continued difficulties and are committed to resolving this immediately.`
};

// Could also trigger additional actions like:
// - Send SMS to management
// - Create Slack alert
// - Email executive team

return [{ json: response }];

// Merge all responses and send final callback
const response = $input.first().json;

// Prepare enhanced callback payload with context
const callbackPayload = {
  correlation_id: response.correlation_id,
  user_id: response.user_id,
  result: response.response_message + `\n\n**Processing Details:**\n- Intent: ${response.intent}\n- Confidence: ${(response.confidence * 100).toFixed(1)}%\n- Urgency: ${response.urgency}\n- Handled by: ${response.handled_by}\n- Actions taken: ${response.actions_taken.length}\n- Is follow-up: ${response.isFollowUp ? 'Yes' : 'No'}\n- Related tickets: ${response.relatedTickets.length > 0 ? response.relatedTickets.join(', ') : 'None'}\n- KB article provided: ${response.kb_article_provided ? 'Yes' : 'No'}`
};

return [{
  json: {
    ...response,
    callback_payload: callbackPayload
  }
}];
