// Get all input items from webhook
const items = $input.all();

// Get the body from the first (and likely only) item from the webhook
const webhookData = items[0].json.body;

// Log to debug
console.log('Full webhook body data:', JSON.stringify(webhookData));

// Extract values
const userMessage = webhookData.message || "No message provided";
const correlationId = webhookData.correlation_id || "no-correlation-id";
const userId = webhookData.user_id || "anonymous";
const callbackUrl = webhookData.callback_url || "";

console.log('Extracted callback_url:', callbackUrl);
console.log('Extracted correlation_id:', correlationId);

// Simulate processing delay
await new Promise(resolve => setTimeout(resolve, 1000));

// Generate simulated response
const responses = [
  `I understand you said: "${userMessage}". This is a test response from the n8n workflow.`,
  `Processing your request: "${userMessage}". The workflow is working correctly!`,
  `Thank you for your message: "${userMessage}". The async communication is functioning properly.`,
  `Received: "${userMessage}". This demonstrates the full flow from React → FastAPI → n8n → FastAPI → React.`
];

const response = responses[Math.floor(Math.random() * responses.length)];

// Add some analysis
const analysis = {
  messageLength: userMessage ? userMessage.length : 0,
  wordCount: userMessage ? userMessage.split(' ').length : 0,
  sentiment: userMessage && userMessage.includes('!') ? 'excited' : 'neutral',
  processingTime: '1.2s'
};

// Return in n8n's required format
return [{
  json: {
    correlation_id: correlationId,
    user_id: userId,
    original_message: userMessage,
    ai_response: response,
    analysis: analysis,
    processed_at: new Date().toISOString(),
    callback_url: callbackUrl
  }
}];

